import React, { useState } from 'react';
import './Pipeline.css';

const Pipeline = () => {
  const [tasks, setTasks] = useState([
    { id: 1, name: 'Build', status: 'pending' },
    { id: 2, name: 'Test', status: 'pending' },
    { id: 3, name: 'Deploy', status: 'pending' },
  ]);

  const handleClick = (taskId) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId
          ? { ...task, status: task.status === 'completed' ? 'pending' : 'completed' }
          : task
      )
    );
  };

  return (
    <div className="pipeline-container">
      <h1 className="pipeline-title">CI/CD Pipeline</h1>
      <div className="pipeline-tasks">
        {tasks.map((task) => (
          <button
            key={task.id}
            className={`pipeline-button ${task.status}`}
            onClick={() => handleClick(task.id)}
          >
            {task.name}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Pipeline;
