import React from 'react';
import ReactFlow, { MiniMap, Controls, Background } from 'reactflow';
import 'reactflow/dist/style.css';

// Моки задач пайплайна
const initialNodes = [
  {
    id: '1',
    type: 'input',
    data: { label: 'Build' },
    position: { x: 250, y: 0 },
    style: { background: '#FFD700', color: '#000', border: '1px solid #333' },
  },
  {
    id: '2',
    data: { label: 'Test' },
    position: { x: 100, y: 150 },
    style: { background: '#1E90FF', color: '#fff', border: '1px solid #333' },
  },
  {
    id: '3',
    data: { label: 'Deploy' },
    position: { x: 400, y: 150 },
    style: { background: '#32CD32', color: '#fff', border: '1px solid #333' },
  },
];

const initialEdges = [
  { id: 'e1-2', source: '1', target: '2', animated: true },
  { id: 'e1-3', source: '1', target: '3', animated: true },
];

const Pipeline = () => {
  return (
    <div style={{ height: 500 }}>
      <ReactFlow
        nodes={initialNodes}
        edges={initialEdges}
        fitView
        attributionPosition="top-right"
      >
        <MiniMap />
        <Controls />
        <Background color="#aaa" gap={16} />
      </ReactFlow>
    </div>
  );
};

export default Pipeline;
